import girlillustration from "assets/illustration/girl.svg"
import styled from "styled-components"
import {PRIMARY_COLOR, SECONDARY_COLOR} from "assets/colorsPallete"

export const Wrapper = styled.div`
    padding: 28rem 0 4rem 0;
    background-image:url(${girlillustration});
    background-size:contain;
    background-position:top;
    background-repeat:no-repeat;
    padding: 28rem 0 4rem 0;
    background-color:${({theme})=>(theme ==="light" ? PRIMARY_COLOR:SECONDARY_COLOR)};
    /* -webkit-mask-image: url(${girlillustration});
    mask-image: url(${girlillustration});

    background-position:top;
    background-repeat:no-repeat;
    -webkit-mask-size: cover;
    mask-size: cover;
    background-color: yellow; */

    @media (max-width:1960px){
        padding:14rem 0 4rem;
    };
`

export const Flex = styled.div`
    display:flex;
    align-items:flex-end;
    justify-content:space-between;

    @media (max-width:600px){
        flex-direction: column;
        text-align:center;
        align-items:center;
    }
`
export const Links = styled.div`
    display:flex;
    align-items:center;

    a  {
            margin:0 0.5rem;
            img{
                margin:0;
            }
            &:first-child, 
            &:last-child{
                margin:0;
            }
    }

`

export const Details = styled.div`
        h5,a,span{
            color:#212121
        }

        @media (max-width: 680px){
            margin-bottom: 2rem;
    }
`;
