    min-height:90vh;


    @media (max-width:1960px){
        padding:1rem 0 1rem;
    };
`

export const Grid = styled.div`
    display:grid;
    /* border:red solid; */
    align-items:center;
    margin:15% 5%;
    /* width:fit-content; */
