    grid-template-columns:${({number})=>`repeat(${number},50%)`};
    @media (max-width:600px){
        grid-template-columns:100%;
        text-align:center;
        align-items:center;
        margin:10% 0;
    };
    div{
        background-color:rgba(255,255,255,0.3);
        font-size:1.2rem;
        button{
            align-items:center;
            margin:0% 0%auto;
        }
    }

`
export const Links = styled.div`
    display:flex;
    align-items:center;

    a  {
            margin:0 0.5rem;
            img{
                margin:0;
            }
            &:first-child, 
            &:last-child{
                margin:0;
            }
    }

`

export const Details = styled.div`
        h5,a,span{
            color:#212121
        }

        @media (max-width: 680px){
            margin-bottom: 2rem;
    }
`;

