import styled from 'styled-components';

export const Button = styled.button`
  cursor: pointer;
  border-radius: 3px;
  /* padding: 0.7rem 2.5rem; */
  padding: ${({padding})=>padding ? `${padding}%` : "0.7rem 2.5rem"};
  border: none;
  -webkit-appearance: none;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  background: #0074d9;
  color: ${({color})=>color ?`${color}` :"#fff"};
  background: ${({backgroundColor})=>backgroundColor ?`${backgroundColor}` : "#0074d9"};
  &:focus {
    outline: none;
  }

  &:disabled {
    background: gray;
  }

  ${({ secondary }) =>
    secondary &&
    `
		background: #001F3F;
	`}
`;
