import styled from 'styled-components';
import homesvgimage from "assets/illustration/homesvgimage.svg"
export const Wrapper = styled.div`
  background: transparent;
  width: 100%;
  background-image:url(${homesvgimage});
  background-position:top;
  background-size:contain;
  min-height:100vh;
  clip-path:red;
`;

export const Overlay = styled.div`
  position: fixed;
  background: rgba(0, 0, 0, 0.7);
  width: 100%;
  height: 100%;
  display: none;
  transition: 0.4s;

  ${({ sidebar }) =>
    sidebar &&
    `
			display: block;
      z-index: 4;	

  `}
  @media (min-width:960px){
    display:none;
  }
`;
