import styled from 'styled-components';
// import homesvgimage from "assets/illustration/homesvgimage.svg"
import homesvgimage from "assets/illustration/girl.svg"
import {HEADER_PRIMARY_COLOR, SECONDARY_COLOR} from "assets/colorsPallete"
import { NULL } from 'node-sass';
export const Wrapper = styled.div`
  /* background: transparent; */
  width: 100%;
  /* background-image:url(${homesvgimage}); */
  position:${({scroll})=>(scroll == "100"?"sticky":NULL)};
  top:0;
  background-size:contain;
  background-repeat:no-repeat;
  /* min-height:100vh; */
  background-color:${({theme})=>(theme === "light"? HEADER_PRIMARY_COLOR :SECONDARY_COLOR)};
`;

export const Overlay = styled.div`
  position: fixed;
  background: rgba(0, 0, 0, 0.7);
  width: 100%;
  height: 100%;
  display: none;
  transition: 0.4s;

  ${({ sidebar }) =>
    sidebar &&
    `
			display: block;
      z-index: 4;	

  `}
  @media (min-width:960px){
    display:none;
  }
`;
