import React, {useContext,useState,useEffect} from "react"
import {ThemeContext} from "providers/ThemeProvider"
// import { Container } from "components/common"
import {Wrapper, Grid} from "./styles"
// import {image_width,image_height} from "assets/Sizes"
import {Button} from "components/common"

let initialState = {letters:""}
export const Landing =()=>{
    let {theme} = useContext(ThemeContext)
    let [typyingState,setTypingState] = useState(initialState)
    var name = "Deaty";

    let typeName = (name, iteration) => {
    // Prevent our code executing if there are no letters left
    if (iteration === name.length)
        return;

    setTimeout(()=> {
        // Set the name to the current text + the next character
        // whilst incrementing the iteration variable
        setTypingState((prev)=>({
            letters: prev.letters + name[iteration++] } )
       )

        // Re-trigger our function
        typeName(name, iteration);
    }, 1000);
}

// Call the function to begin the typing process
useEffect(()=>{
    typeName(name, 0)
},[])
;
    return(
        <>
            <Wrapper theme={theme}  > 
                <Grid number={1}  >

                    <div>
                        <h1>
                            {/* The Goggle Cloud <br/> */}
                            {typyingState.letters}
                            <br/>
                            Dey Community project <br/>
                            {/* Health Group Project Team 35<br/> */}
                            Heath-MeetUp
                        </h1>
                        <h4>
                            Do you fell empty, sick and dejected from your community due to an
                            illness, emotional disorder, pyschological troma and other health situation,
                            come join us at Health-MeetUp where you meet and interract with people going throungh your situation,
                            schedule and take consulation from the best doctors around......
                        </h4>
                        <Button>Get Started</Button>
                    </div>


                </Grid>


            </Wrapper>
        </>
    )
}
